import java.time.LocalDate;import java.util.List;import entities.Status;import java.util.Random;public class Order {    private final Long id;    private final Status status;    private final LocalDate dateOrder;    private final LocalDate dateDelivery;    private final List<Product> products;    private final Customers customers;    Random random = new Random();    public Order( Status status, LocalDate dateOrder, List<Product> products, Customers customers)  {        this.id = random.nextLong(1,1000000000);        this.status = status;        this.dateOrder = dateOrder;        this.products = products;        this.customers = customers;        this.dateDelivery = dateOrder.plusDays(5);    }    public String getCategory() {        return category;    }    public static List<Order> sameCategoryOrder(List<Order> products) {        return products.stream().filter(prod -> prod.getCategory().equals("Baby")).toList();    }    public List<Product> getProducts() {        return this.products;    }    @Override    public String toString() {        return "ID: " + id + "\nStatus: " + status + "\nDate Order: " + dateOrder + "\nDate Delivery: " + dateDelivery + "\nProducts: " + products + "\nCustomers: " + customers;    }}